package javaPackage;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =21;
		
		if(a%2 == 0)
		{
			System.out.println("It is a even number "+a);
		}
		else
		{
			System.out.println("It is a odd number "+a);
		}
	}

}
